echo " , company, role, submitted, on, status, updated on" > profDocs/submissions-summary.csv;counter=0;submitted=0;declined=0;interview=0;pending=0;for folder in profDocs/submissions/*;do if [ -f $folder"/submitted.txt" ];then submitted=$((submitted+1));fi;if [ -f $folder"/declined.txt" ];then declined=$((declined+1));elif [ -f $folder"/interview.txt" ];then interview=$((interview+1));else pending=$((pending+1));fi;echo $((++counter))", "$(echo $(IFS='_' read -r -a array <<< "$(IFS='/' read -r -a array <<< "$folder" && echo "${array[2]}")" && echo "${array[0]}") | tr '-' ' ')", "$(echo $(IFS='_' read -r -a array <<< "$(IFS='/' read -r -a array <<< "$folder" && echo "${array[2]}")" && echo "${array[1]}") | tr '-' ' ')", "$(if [ -f $folder"/submitted.txt" ];then echo "yes, "$(date -r $folder"/DiStasioLuca-cover.pdf" "+%Y-%m-%d");else echo "no, ";fi)", "$(if [ -f $folder"/declined.txt" ];then echo "declined, "$(date -r $folder"/declined.txt" "+%Y-%m-%d");elif [ -f $folder"/interview.txt" ];then echo "interview, "$(date -r $folder"/interview.txt" "+%Y-%m-%d");else echo "pending, ";fi) >> profDocs/submissions-summary.csv;done;echo $submitted" submitted, "$pending" pending, "$interview" interviewed, "$declined" declined" > profDocs/submissions-review.csv;echo $(bc <<< 100*$(($interview+$declined))/$submitted)"% of submissions received a response, "$(bc <<< 100*$interview/$(($interview+$declined)))"% of this was positive (invitation to interview)" >> profDocs/submissions-review.csv;cat profDocs/submissions-review.csv
